// src/components/LineChart.js
import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import 'chartjs-adapter-date-fns';
import './LineChart.css'; // Assuming you have a CSS file for styling

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const LineChart = () => {
    const [chartData, setChartData] = useState({
        labels: [],
        datasets: [],
    });

    const fetchTrafficData = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/traffic_data/time_series');
            const data = await response.json();
            if (data && Array.isArray(data)) {
                setChartData({
                    labels: data.map(item => new Date(item.timestamp).toLocaleString()),
                    datasets: [
                        {
                            label: 'Average Traffic Speed',
                            data: data.map(item => item.average_speed),
                            backgroundColor: 'rgba(54, 162, 235, 0.2)',
                            borderColor: 'rgba(54, 162, 235, 1)',
                            borderWidth: 1,
                            tension: 0.4,
                        },
                    ],
                });
            }
        } catch (error) {
            console.error('Error fetching traffic data:', error);
        }
    };

    useEffect(() => {
        fetchTrafficData();
    }, []);

    const options = {
        responsive: true,
        maintainAspectRatio: false, // Add this to maintain aspect ratio
        plugins: {
            legend: {
                display: true,
            },
            title: {
                display: true,
                text: 'Traffic Speed Over Time',
            },
        },
        scales: {
            x: {
                type: 'time',
                time: {
                    unit: 'minute', // Change this to 'hour' or other appropriate unit
                    stepSize: 15, // Adjust step size to avoid label overlap
                    displayFormats: {
                        minute: 'h:mm a', // Adjust this format to match your preference
                    },
                },
                title: {
                    display: true,
                    text: 'Time',
                },
            },
            y: {
                beginAtZero: true, // This ensures the chart begins at zero
                title: {
                    display: true,
                    text: 'Speed (mph)',
                },
            },
        },
    };
    

    return (
        <div className="line-chart-container">
            <h2>Real-Time Traffic Analysis</h2>
            <p>This chart displays the average traffic speed over time.</p>
            <div className="chart">
                <Line data={chartData} options={options} />
            </div>
        </div>
    );
};

export default LineChart;
