// src/components/Heatmap.js
import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet.heat';

const Heatmap = ({ trafficData }) => {
    const mapRef = useRef(null);

    useEffect(() => {
        if (!mapRef.current) {
            mapRef.current = L.map('heatmap').setView([40.7128, -74.0060], 13);
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapRef.current);
        }

        const heatPoints = trafficData.map(data => 
            [data.location.latitude, data.location.longitude, data.intensity] // intensity can be traffic volume or speed
        );
        L.heatLayer(heatPoints, { radius: 25 }).addTo(mapRef.current);
    }, [trafficData]);

    return <div id="heatmap" style={{ height: '500px' }} />;
};

export default Heatmap;
