import React, { useState, useEffect } from 'react';
import LineChart from './LineChart';
import PieChart from './PieChart';
import Heatmap from './Heatmap';
import DateRangePicker from './DateRangePicker';
import Dropdown from './Dropdown';
import './Dashboard.css';

const Dashboard = () => {
    const [dateRange, setDateRange] = useState({ startDate: new Date(), endDate: new Date() });
    const [selectedCondition, setSelectedCondition] = useState('All');
    const [trafficData, setTrafficData] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        console.log('Date Range:', dateRange);
        console.log('Selected Condition:', selectedCondition);
        const fetchData = async () => {
            setIsLoading(true);
            setError(null);

            const params = new URLSearchParams({
                startDate: dateRange.startDate ? dateRange.startDate.toISOString() : '',
                endDate: dateRange.endDate ? dateRange.endDate.toISOString() : '',
                condition: selectedCondition
            });

            try {
                const response = await fetch(`http://127.0.0.1:5000/traffic_data`);
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setTrafficData(data);
            } catch (error) {
                setError('Failed to fetch data: ' + error.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchData();
    }, [dateRange, selectedCondition]);

    if (isLoading) return <div className="loading">Loading...</div>;
    if (error) return <div className="error">Error: {error}</div>;

    return (
        <div className="dashboard">
            <header className="dashboard-header">
                <h1>Traffic Analysis Dashboard</h1>
            </header>
            <aside className="dashboard-filters">
                <DateRangePicker onChange={setDateRange} />
                <Dropdown 
                    label="Traffic Condition" 
                    options={['All', 'Congested', 'Slow-moving', 'Free-flowing']} 
                    onChange={setSelectedCondition} 
                />
                {/* Additional filter components */}
            </aside>
            <main className="dashboard-charts">
                {trafficData && <LineChart data={trafficData} />}
                {trafficData && <PieChart data={trafficData} />}
                {trafficData && <Heatmap data={trafficData} />}
                {/* Additional chart components */}
            </main>
        </div>
    );
};

export default Dashboard;
