// src/components/TrafficMap.js
import React, { useEffect, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const TrafficMap = ({ trafficData }) => {
    const [map, setMap] = useState(null);

    useEffect(() => {
        if (!map) {
            const mapInstance = L.map('map').setView([40.7128, -74.0060], 13);
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapInstance);
            setMap(mapInstance);
        }

        // Add markers for each traffic data point
        if (map && trafficData) {
            trafficData.forEach(data => {
                if (data.location) {
                    L.marker([data.location.latitude, data.location.longitude])
                        .addTo(map)
                        .bindPopup(`Speed: ${data.speed} mph`);
                }
            });
        }
    }, [map, trafficData]);

    return <div id="map" style={{ height: '500px' }} />;
};

export default TrafficMap;
