import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet.heat';
import './Heatmap.css'; // Assuming you have a CSS file for styling

const Heatmap = () => {
    const mapRef = useRef(null);
    const containerRef = useRef(null);

    useEffect(() => {
        if (!mapRef.current && containerRef.current) {
            mapRef.current = L.map(containerRef.current).setView([40.7128, -74.0060], 13);
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapRef.current);
        }

        fetch('http://127.0.0.1:5000/traffic_data/heatmap')
            .then(response => response.json())
            .then(data => {
                const heatPoints = data.map(point => [point.latitude, point.longitude, point.intensity]);
                L.heatLayer(heatPoints, { radius: 25, blur: 15 }).addTo(mapRef.current);
            })
            .catch(error => console.error('Error fetching heatmap data:', error));

        return () => {
            if (mapRef.current) {
                mapRef.current.remove();
                mapRef.current = null;
            }
        };
    }, []);

    return (
        <div className="heatmap-container">
            <h2>Traffic Heatmap</h2>
            <p>This heatmap visualizes the concentration of traffic throughout the city.</p>
            <div ref={containerRef} className="heatmap" />
        </div>
    );
};

export default Heatmap;
